css:
@font-face {
  font-family: 'Certificate';
  src: url("/assets/lms/css/certificate.ttf");
  font-weight: 700;
}

.certificate-format {
    border-radius: 8px;
	max-width: 11in;
	max-height: 8.5in;
	color: var(--gray-900);
    margin: 0.5rem;
}

.certificate-text {
	font-family: 'Certificate';
	font-size: 3rem;
}

.certified-text {
    font-family: 'Sen'; 
    font-size: 1.2rem;
}
.inner-border {
    background: url(/assets/lms/images/certificate-bg.png);
    background-repeat: round;
    background-size: cover;
	max-width: 11in;
	/*height: 8.5in;*/
    border: 24px solid black;
    border-radius: 8px;
    text-align: center;
    padding: 0.5rem 0.5rem;
}

.certificate-name {
    font-family: 'Sen';
    font-size: 2.5rem;
}

.certificate-footer {
    margin: 2rem auto 0;
    width: 100%;
    padding: 0;
}

.certificate-footer-item {
    color: #192734;
}

.cursive-font {
    font-family: cursive;
    font-weight: 600;
}

.certificate-divider {
    margin: 0.5rem 0;
    border-top: 1px solid;
}

.certificate-expiry {
    margin-left: 2rem;
}
.certificate-content {
	padding: 0.5rem;
	border-radius:0px;
}
.training-details{
    font-size: 1.2rem;
}

.certificate-no{
    font-family: Arial, sans-serif;
}

#certificate_format .row:not(.section-break) {
    margin-top: 0px !important;
}
/**/

 /* Apply the translate and rotate transformation 
           for our text to look curved  */ 
.C1 {
    transform: translate(25px, -6px) rotate(-17deg);
}

.e1 {
    transform: translate(20px, -12px) rotate(-17deg);
}

.r {
    transform: translate(18px, -18px) rotate(-14deg);
}

.t1 {
    transform: translate(15px, -23px) rotate(-13deg);
}

.i1 {
    transform: translate(12px, -27px) rotate(-11deg);
}

.f1 {
    transform: translate(9px, -29px) rotate(-10deg);
}

.i2 {
    transform: translate(5px, -32px) rotate(-7deg);
}

.c2 {
    transform: translate(3px, -34px) rotate(-7deg);
}

.a {
    transform: translate(1px, -36px) rotate(-1deg);
}

.t2 {
    transform: translate(-2px, -36px) rotate(-1deg);
}

.e2 {
    transform: translate(-4px, -37px) rotate(0deg);
}

.o1 {
    transform: translate(2px, -37px) rotate(0deg);
}

.f2 {
    transform: translate(-2px, -37px) rotate(0deg);
}

.C3 {
    transform: translate(4px, -34px) rotate(3deg);
}

.o2 {
