}

.m {
    transform: translate(-4px, -32.31px) rotate(5deg);
}

.p {
    transform: translate(-6px, -29.98px) rotate(4deg);
}

.l {
    transform: translate(-10px, -28.65px) rotate(8deg);
}

.e3 {
    transform: translate(-14px, -26.32px) rotate(8deg);
}

.t3 {
    transform: translate(-15px, -21.99px) rotate(10deg);
}

.i3 {
    transform: translate(-18px, -18.66px) rotate(14deg);
}

.o3 {
    transform: translate(-22px, -14.33px) rotate(14deg);
}

.n {
    transform: translate(-27px, -7px) rotate(20deg);
